import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Authorization } from '../components/Authorization';
import "../styles/Home.css";

export default function Listings() {
    Authorization();

    const [listings, setListings] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [newListing, setNewListing] = useState({ name: '', description: '', image: null });
    const [isEditing, setIsEditing] = useState(false);
    const [editingIndex, setEditingIndex] = useState(null);

    // Fetch all listings from the backend
    useEffect(() => {
        fetch('/api/listings')
            .then(response => response.json())
            .then(data => setListings(data))
            .catch(error => console.error('Error fetching listings:', error));
    }, []);

    // Open and close modal
    const toggleModal = () => {
        setShowModal(!showModal);
    };

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewListing({ ...newListing, [name]: value });
    };

    // Handle image upload
    const handleImageUpload = (e) => {
        if (e.target.files[0]) {
            setNewListing({ ...newListing, image: URL.createObjectURL(e.target.files[0]) });
        }
    };

    // Handle form submission for adding or editing a listing
    const handleSubmit = () => {
        const listingData = { ...newListing };

        if (isEditing) {
            fetch(`/api/listings/${listings[editingIndex].id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(listingData),
            })
                .then(() => {
                    const updatedListings = [...listings];
                    updatedListings[editingIndex] = { ...updatedListings[editingIndex], ...listingData };
                    setListings(updatedListings);
                })
                .catch(error => console.error('Error editing listing:', error));
        } else {
            fetch('/api/listings', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(listingData),
            })
                .then(response => response.json())
                .then(data => {
                    setListings([...listings, { id: data.id, ...listingData }]); // Assuming backend returns the added listing with an ID
                })
                .catch(error => console.error('Error adding listing:', error));
        }

        setNewListing({ name: '', description: '', image: null });
        setIsEditing(false);
        toggleModal();
    };

    // Handle delete listing
    const handleDelete = (id) => {
        fetch(`/api/listings/${id}`, {
            method: 'DELETE',
        })
            .then(() => {
                setListings(listings.filter(listing => listing.id !== id));
            })
            .catch(error => console.error('Error deleting listing:', error));
    };

    return (
        <div>
            <h1 className="header">Welcome to the Listings Page!</h1>
            <div className="body">
                <Link to="/Home">Home</Link>
            </div>

            <div className="add-listing-button-container">
                <button onClick={() => { setIsEditing(false); toggleModal(); }} className="add-listing-button">
                    Add Listing
                </button>
            </div>

            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>{isEditing ? "Edit Listing" : "Add New Chair Listing"}</h2>
                        <label>Name:</label>
                        <input
                            type="text"
                            name="name"
                            value={newListing.name}
                            onChange={handleInputChange}
                        />
                        <label>Description:</label>
                        <input
                            type="text"
                            name="description"
                            value={newListing.description}
                            onChange={handleInputChange}
                        />
                        <label>Picture:</label>
                        <input
                            type="file"
                            accept="image/*"
                            onChange={handleImageUpload}
                        />
                        <button onClick={handleSubmit} className="submit-button">
                            {isEditing ? "Save Changes" : "Done"}
                        </button>
                        <button onClick={toggleModal} className="close-button">Cancel</button>
                    </div>
                </div>
            )}

            <div className="listings-display">
                <h2>Your Listings</h2>
                <div className="grid-container">
                    {listings.map((listing) => (
                        <div key={listing.id} className="listing-item">
                            <h3>{listing.name}</h3>
                            <p>{listing.description}</p>
                            {listing.image && <img src={listing.image} alt={listing.name} />}
                            <button onClick={() => handleDelete(listing.id)} className="delete-button">Delete</button>
                            <button onClick={() => {
                                setNewListing({ name: listing.name, description: listing.description, image: listing.image });
                                setEditingIndex(listings.findIndex(item => item.id === listing.id));
                                setIsEditing(true);
                                toggleModal();
                            }} className="listing-btns">Edit</button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
