import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Authorization } from '../components/Authorization';
import "../styles/Home.css";

export default function Listings() {
    Authorization();

    // Sample state to hold listings
    const [listings, setListings] = useState([]);
    const [newListing, setNewListing] = useState({ title: '', imageUrl: '' });
    const [editMode, setEditMode] = useState(false);
    const [currentListingId, setCurrentListingId] = useState(null);

    // Function to handle adding a new listing
    const handleAddListing = (e) => {
        e.preventDefault();
        if (editMode) {
            // Update existing listing
            setListings(listings.map((listing) => 
                listing.id === currentListingId ? { ...listing, ...newListing } : listing
            ));
            setEditMode(false);
            setCurrentListingId(null);
        } else {
            // Add new listing
            const newId = listings.length ? listings[listings.length - 1].id + 1 : 1; // Incrementing ID
            setListings([...listings, { id: newId, ...newListing }]);
        }
        setNewListing({ title: '', imageUrl: '' }); // Reset form
    };

    // Function to handle deleting a listing
    const handleDeleteListing = (id) => {
        setListings(listings.filter((listing) => listing.id !== id));
    };

    // Function to handle editing a listing
    const handleEditListing = (listing) => {
        setNewListing({ title: listing.title, imageUrl: listing.imageUrl });
        setEditMode(true);
        setCurrentListingId(listing.id);
    };

    return (
        <div>
            <h1 className="header">Welcome to the Listings Page!</h1>
            <div className="body">
                <Link to="/Home">Home</Link>
            </div>

            {/* Form for adding/editing listings */}
            <form onSubmit={handleAddListing}>
                <input 
                    type="text" 
                    placeholder="Title" 
                    value={newListing.title} 
                    onChange={(e) => setNewListing({ ...newListing, title: e.target.value })} 
                    required 
                />
                <input 
                    type="text" 
                    placeholder="Image URL" 
                    value={newListing.imageUrl} 
                    onChange={(e) => setNewListing({ ...newListing, imageUrl: e.target.value })} 
                    required 
                />
                <button type="submit">{editMode ? 'Update Listing' : 'Add Listing'}</button>
            </form>

            <div className="listings-display">
                <div className="grid-container">
                    {listings.map((listing) => (
                        <div key={listing.id} className="listing-item">
                            <h2>{listing.title}</h2>
                            <img src={listing.imageUrl} alt={listing.title} />
                            <button className="delete-button" onClick={() => handleDeleteListing(listing.id)}>Delete</button>
                            <button className="listing-btns" onClick={() => handleEditListing(listing)}>Edit</button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
